=begin

def find_market(input)

    marker = ''
    i = 0

    until input.eof?

      if marker.length < 4
        add character to marker
      elsif all characters the same?
        return i # we found marker
      else
        remove character from front
      end

      i = i + 1
    end
=end

def is_marker?(marker)
  marker.split(//).uniq.length == 4
end

def find_marker(input)
  marker = ''

  index = 0

  until index >= input.length
    char = input[index]
    if marker.length < 4
      marker = marker + char
    index = index + 1
    elsif is_marker?(marker)
      return index
    else
      marker = marker[1..]
    end
  end

  -1
end

puts find_marker("")